<?php	require 'include/mod.check_service_permission.inc.php';	require "init_smarty.php";		$content = "../templates/mod_modify_delete_content.html";		if(isset($_GET['query'])){		require "include/search.inc.php";		if (isset($_SESSION['search'][1])) {			$smarty->assign("artists", $_SESSION['search'][1]);		}else{			$smarty->assign("artists", array());		}		if (isset($_SESSION['search'][2])) {			$smarty->assign("songs", $_SESSION['search'][2]);		}else{			$smarty->assign("songs", array());		}		if (isset($_SESSION['search'][0])) {			$smarty->assign("albums", $_SESSION['search'][0]);		}else{			$smarty->assign("albums", array());		}		//Genero i numeretti corrispondenti alle pagine dei risultati		//Calcolo le pagine necessarie a mostrare i risultati		if (isset($_SESSION['search'][1])) {			$pagesNeededArtists = ceil(count($_SESSION['search'][1]) / 3);		} else {			$pagesNeededArtists = 0;		}		if (isset($_SESSION['search'][0])) {			$pagesNeededAlbum = ceil(count($_SESSION['search'][0]) / 3);		} else {			$pagesNeededAlbum = 0;		}		if (isset($_SESSION['search'][2])) {		$pagesNeededSongs = ceil(count($_SESSION['search'][2]) / 3);		} else {			$pagesNeededSongs = 0;		}				$pagesNeeded = max($pagesNeededArtists, $pagesNeededAlbum, $pagesNeededSongs);				$smarty->assign("query", $_GET['query']);		$smarty->assign("page_counter", $pagesNeeded);	}		$smarty->assign("page_counter", 0);		require "include/mod.set_logged_header.inc.php";		$smarty->assign("content", $content);		$smarty->display("../templates/mod_frame_public.html");?>